package org.capgemini.datatypes;

public class ByteDatatypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int xyz=123;
		long abc= 2147043640L;
		//long value=L
		char num='r';
		//single '' = char
		float d=12.63f;
		double g=41.9;
		String str="Yashwanth";
		boolean val=true;
		System.out.println("The Value is :"+xyz);
		System.out.println("The long Value is :"+abc);
		System.out.println("The char is :"+num);
		System.out.println("The float is :"+d);
		System.out.println("The double is :"+g);
		System.out.println("The string is :"+str);
		System.out.println("The boolean Value is :"+val);

	}

}
